let clock = function(){

        const secondHand = document.querySelector('.hand.second');
        const minuteHand = document.querySelector('.hand.minute');
        const hourHand   = document.querySelector('.hand.hour');
        const $ = document.querySelector(':root');

        this.setColorSecond = function(color){
            $.style.setProperty('--second-color', color);
        }
        this.setColorMinute = function(color){
            $.style.setProperty('--minute-color', color);
        }
        this.setColorHour = function(color){
            $.style.setProperty('--hour-color', color);
        }
        this.setWidth = function(width){
            $.style.setProperty('--clock-w', width);
        }
        this.setHeight = function(height){
            $.style.setProperty('--clock-h', height);
        }
        this.setPosition = function(x,y){
            $.style.setProperty('--clock-left', x);
            $.style.setProperty('--clock-top', y);
        }
        this.setOpacity = function(opacity){
            $.style.setProperty('--clock-opacity', opacity);
        }
        this.setRadius = function(radius){
            $.style.setProperty('--clock-radius', radius);
        }
        this.setBkg = function(bkg){
            $.style.setProperty('--clock-bkg', bkg);
        }
        this.setHandPosition = function(x,y){
            $.style.setProperty('--hand-left', x);
            $.style.setProperty('--hand-top', y);
        }
        this.setHandHeight = function(height){
            $.style.setProperty('--hand-height', height);
        }
        this.setHandWidth = function(width){
            $.style.setProperty('--hand-width', width);
        }
        this.setHandOrigin = function(origin){
            $.style.setProperty('--hand-origin', origin);
        }
        this.setHandTransition = function(trans){
            $.style.setProperty('--hand-transition', trans);
        }
        this.setHandRadius = function(radius){
            $.style.setProperty('--hand-radius', radius);
        }
        this.setHandTimingFunction = function(trans){
            $.style.setProperty('--hand-transition-timing-function', trans);
        }


        this.setClock = function(){
            const degUpdate = 0;
            const now = new Date();
            const seconds = now.getSeconds();
            const minutes = now.getMinutes();
            const hours = now.getHours();
            const secondDeg = ((seconds/60)*360)+degUpdate;
            const minuteDeg = ((minutes/60)*360)+degUpdate;
            const hourDeg   = ((hours/24)*360)+degUpdate;

            secondHand.style.transform = "rotate("+secondDeg+"deg)";
            minuteHand.style.transform = "rotate("+minuteDeg+"deg)";
            hourHand.style.transform   = "rotate("+hourDeg+"deg)";

        }

      setInterval(setClock, 1000);
}

clock();
